--  DDL for HYDRA_PLACEMENT TABLE AND PLACEMENT_ID_SEQUENCE
--------------------------------------------------------
--In case a table needs to be created. Commented out because it creates more problems than it solves.
DROP TABLE IF EXISTS HYDRA_PLACEMENT;
CREATE TABLE IF NOT EXISTS HYDRA_PLACEMENT (PLACEMENT_ID INT(11) PRIMARY KEY, START_DATE TIMESTAMP, END_DATE TIMESTAMP, CLIENT_ID INT(11), END_CLIENT_ID INT(11), ASSOCIATE_ID INT(11));
DROP SEQUENCE IF EXISTS PLACEMENT_ID_SEQUENCE;
CREATE SEQUENCE  IF NOT EXISTS  PLACEMENT_ID_SEQUENCE  MINVALUE 500  INCREMENT BY 1 START WITH 500;



